package sample;

public class RelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10, b=8; 	
		boolean e=a>b;
		System.out.println("The Value Of a "+ a+" b "+b+" respectively");
		System.out.println("The Value Of a>b " + e);
		e=a<b;
		System.out.println("The Value Of a<b " + e);
		e=(a<=b);
		System.out.println("The Value Of a<=b " + e);
		e=(a>=b);
		System.out.println("The Value Of a>=b " + e);
		e=(a<=b);
		System.out.println("The Value Of a<=b " + e);
		e=(a==b);
		System.out.println("The Value Of a==b " + e);
		e=(a!=b);
		System.out.println("The Value Of a!=b " + e);


	}

}
